import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons"
import styled from "styled-components"
import './myStyles.css'
import {sliderItems} from "../data"

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
    overflow: hidden;
`

const Arrow = styled.div`
    width:50px;
    height: 50px;
    background-color: #E879BB;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=> props.direction === "left" && "10px"};
    right: ${props=> props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
    z-index: 2;
`

const Wrapper = styled.div`
    height:100%;
    display: flex;
    transform: translateX(0vw)
`
const Slide = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
    background-color: #${props=>props.bg};

`

const Landing = styled.div`
    width: 100vw;
    height: 100vh;

`
const ImgContainer = styled.div`
    height: 100%;
    flex:1;
`

const Image = styled.img`
height: 80%;
`

const InfoContainer = styled.div`
    flex:1;
    padding:50px;
`
const Title = styled.h1`
    font-size 70px;
`
const Desc = styled.p`
    margin: 50px 0px;
    font-size: 20px;
    font-weigth: 500;
    letter-spacing: 3px;
`
const Button = styled.button`
    padding: 10px;
    font-size: 20px;
    background-color: #E879BB;
    cursor: pointer;
    border: solid 1px transparent;
    border-radius: 50px;
`

const Slider = () => {
        const [slideIndex,setSLideIndex ] = useState(0);
        const handleClick = (direction) => {
            if(direction==="left"){
                setSLideIndex(slideIndex > 0 ? slideIndex-1 : 2)
            } else {
                setSLideIndex(slideIndex > 0 ? slideIndex-1 : 2)
            }
        };
    return (
        <Container>
            <Arrow direction="left" onClick={()=>handleClick("left")}>
                <ArrowLeftOutlined/>
            </Arrow>
            <Wrapper>
            {sliderItems.map(item=>(
                
                <Slide bg='item.bg'>
                    <ImgContainer>
                    <Image src={item.img}/>
                    </ImgContainer>
                    <InfoContainer>
                        <Title>{item.title}</Title>
                        <Desc>{item.desc}</Desc>
                        <Button>SHOP NOW!</Button>
                    </InfoContainer>
                </Slide>
            ))}
           
           
            <Landing className="landing">
                <ImgContainer>
                </ImgContainer>
                <InfoContainer>
                    <Image className="landingImg" src="./images/landing-logo.png"/>
                    <Desc>DONT COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS</Desc>
                    <Button>Shop Now!</Button>
                </InfoContainer>
            </Landing>

            <Slide bg='e0cb8c'>
                <ImgContainer>
                <Image src="./images/slide-02.jpg"/>
                </ImgContainer>
                <InfoContainer>
                    <Title>New Styles</Title>
                    <Desc>NEW ARRIVAL ALWAYS INCOMING</Desc>
                    <Button>SHOP NOW!</Button>
                </InfoContainer>
            </Slide>

            <Slide bg='a4d7f4'>
                <ImgContainer>
                <Image src="./images/slide-03.jpg"/>
                </ImgContainer>
                <InfoContainer>
                    <Title>Hoodies and SWEAT PANTS</Title>
                    <Desc>DONT COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS</Desc>
                    <Button>Shop Now!</Button>
                </InfoContainer>
            </Slide>
            </Wrapper>
            <Arrow direction="right" onClick={()=>handleClick("right")}>
                <ArrowRightOutlined/>
            </Arrow>
        </Container>
    )
}

export default Slider
