import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons"
import styled from "styled-components"
import './myStyles.css'

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
`

const Arrow = styled.div`
    width:50px;
    height: 50px;
    background-color: #E879BB;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=> props.direction === "left" && "10px"};
    right: ${props=> props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
`

const Wrapper = styled.div`
    height:100%;
`
const Slide = styled.div`
    display: flex;
    align-items: center;

`

const Landing = styled.div`
    width: 100vw;
    height: 100vh;

`
const ImgContainer = styled.div`

    flex:1;
    max-height: 10rem;
    max-width: 1700px
`

const Image = styled.img`

`

const InfoContainer = styled.div`
    flex:1
    padding:50px;
`
const Title = styled.h1`
    font-size 70px;
`
const Description = styled.p`
    margin: 50px 0px;
    font-size: 20px;
    font-weigth: 500;
    letter-spacing: 3px;
`
const Button = styled.button`
    padding: 10px;
    font-size: 20 px;
    background-color: #E879BB;
    cursor: pointer
`

const Slider = () => {
    return (
        <Container>
            <Arrow direction="left">
                <ArrowLeftOutlined/>
            </Arrow>
            <Wrapper>
            <Landing className="landing">
                <ImgContainer>
                </ImgContainer>
                <InfoContainer>
                    {/* <Title>Summer Sale</Title> */}
                    <Image className="landingImg" src="./images/landing-logo.png"/>
                    <Description>DONT COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS</Description>
                    <Button>Shop Now!</Button>
                </InfoContainer>
            </Landing>

            <Slide>
                <ImgContainer>
                <Image src="./images/slide-02.jpg"/>
                </ImgContainer>
                <InfoContainer>
                    {/* <Title>Summer Sale</Title> */}
                    <Image src="./images/landing-logo.png"/>
                    <Description>DONT COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS</Description>
                    <Button>Shop Now!</Button>
                </InfoContainer>
            </Slide>

            <Slide>
                <ImgContainer>
                <Image src="./images/slide-03.jpg"/>
                </ImgContainer>
                <InfoContainer>
                    {/* <Title>Summer Sale</Title> */}
                    <Image src="./images/landing-logo.png"/>
                    <Description>DONT COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS</Description>
                    <Button>Shop Now!</Button>
                </InfoContainer>
            </Slide>
            </Wrapper>
            <Arrow direction="right">
                <ArrowRightOutlined/>
            </Arrow>
        </Container>
    )
}

export default Slider
